var makeRequestPacket = require('../network/packet').makeRequestPacket;
var cmd = require('./cmd');

module.exports = {
  requestHandShake: function()
  {
    var that = makeRequestPacket(cmd.HAND_SHAKE, function() {
      this.writeString('');
    });
    that.controlId = 0;
    return that;
  },

  requestLogin: function()
  {
    return makeRequestPacket(cmd.LOGIN, function() {
      this.writeString('thacdu');
    });
  },

  requestPing: function() {
    return makeRequestPacket(cmd.PING, function() {});
  }
}
